import { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import { Menu, X, Heart, PawPrint } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import PrimaryButton from "@/components/ui/PrimaryButton";

const PlayfulNavbar = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [scrolled, setScrolled] = useState(false);
    const location = useLocation();

    useEffect(() => {
        const handleScroll = () => {
            setScrolled(window.scrollY > 50);
        };
        window.addEventListener("scroll", handleScroll);
        return () => window.removeEventListener("scroll", handleScroll);
    }, []);

    // Close mobile menu when route changes
    useEffect(() => {
        setIsOpen(false);
    }, [location.pathname]);

    const navLinks = [
        { name: "Home", path: "/" },
        { name: "Browse Pets", path: "/browse" },
        { name: "Essentials", path: "/pet-essentials" },
        { name: "Stories", path: "/success-stories" },
        { name: "Patrons", path: "/sponsors" },
        { name: "About", path: "/about" },
    ];

    return (
        <header className="fixed top-0 left-0 right-0 z-50 p-4">
            <div className="w-[95%] md:w-[90%] mx-auto">
                {/* Navbar Island */}
                <div className={`rounded-[2rem] px-6 py-4 transition-all duration-300 ${scrolled || isOpen
                    ? 'bg-white/90 backdrop-blur-md border border-white/50 shadow-soft'
                    : 'bg-transparent'
                    }`}>
                    <div className="flex items-center justify-between">
                        {/* Logo */}
                        <Link to="/" className="flex items-center gap-2 group">
                            <div className="bg-playful-coral text-white p-2 rounded-xl rotate-3 group-hover:rotate-6 transition-transform duration-300 shadow-sm">
                                <PawPrint className="h-6 w-6 fill-current" />
                            </div>
                            <span className="text-2xl font-heading font-black tracking-tight text-playful-text">
                                Adopt<span className="text-playful-coral">Dont</span>Shop
                            </span>
                        </Link>

                        {/* Desktop Navigation */}
                        <div className="hidden lg:flex items-center justify-center flex-1">
                            <nav className="flex items-center space-x-1">
                                {navLinks.map((link) => (
                                    <Link
                                        key={link.name}
                                        to={link.path}
                                        className={`relative font-bold transition-all duration-300 hover:scale-105 px-4 py-2 rounded-full hover:shadow-sm ${location.pathname === link.path
                                            ? 'bg-playful-yellow text-playful-text shadow-sm'
                                            : scrolled
                                                ? 'text-gray-600 hover:bg-playful-cream hover:text-playful-coral'
                                                : 'text-gray-700 hover:bg-white/80 hover:text-playful-coral'
                                            }`}
                                    >
                                        {link.name}
                                    </Link>
                                ))}
                            </nav>
                        </div>

                        {/* CTA Buttons */}
                        <div className="hidden lg:flex items-center space-x-3">
                            <Link to="/list-pet">
                                <PrimaryButton size="md" className="shadow-lg hover:shadow-xl">
                                    List a Pet
                                </PrimaryButton>
                            </Link>
                        </div>

                        {/* Mobile Menu Button */}
                        <button
                            className={`lg:hidden p-2 rounded-xl transition-all duration-300 ${isOpen
                                ? 'bg-playful-cream text-playful-coral'
                                : scrolled ? 'text-gray-600 hover:bg-gray-100' : 'text-gray-700 bg-white/50 hover:bg-white'
                                }`}
                            onClick={() => setIsOpen(!isOpen)}
                        >
                            {isOpen ? <X className="h-6 w-6" /> : <Menu className="h-6 w-6" />}
                        </button>
                    </div>

                    {/* Mobile Menu */}
                    <div
                        className={`lg:hidden overflow-hidden transition-all duration-300 ease-in-out ${isOpen
                            ? "max-h-[500px] opacity-100 mt-4"
                            : "max-h-0 opacity-0"
                            }`}
                    >
                        <nav className="flex flex-col space-y-2 pb-2">
                            {navLinks.map((link) => (
                                <Link
                                    key={link.name}
                                    to={link.path}
                                    className={`py-3 px-4 rounded-xl font-bold transition-all duration-300 ${location.pathname === link.path
                                        ? 'bg-playful-cream text-playful-coral'
                                        : 'text-gray-600 hover:bg-gray-50'
                                        }`}
                                >
                                    {link.name}
                                </Link>
                            ))}

                            {/* Mobile CTA Buttons */}
                            <div className="pt-4">
                                <Link to="/list-pet">
                                    <PrimaryButton className="w-full justify-center shadow-md">
                                        List a Pet
                                    </PrimaryButton>
                                </Link>
                            </div>
                        </nav>
                    </div>
                </div>
            </div>
        </header>
    );
};

export default PlayfulNavbar;

import React from 'react';
import { motion } from 'framer-motion';
import { Heart, MapPin, Info } from 'lucide-react';
import PrimaryButton from '@/components/ui/PrimaryButton';
import { Link } from 'react-router-dom';

interface PetProps {
  id: number;
  name: string;
  breed: string;
  age: number | string;
  location: string;
  image: string;
  type: string;
}

const PetCard = ({ id, name, breed, age, location, image, type }: PetProps) => {
  return (
    <motion.div
      whileHover={{ y: -10 }}
      className="group relative bg-white rounded-3xl overflow-hidden shadow-soft hover:shadow-hover transition-all duration-300 border-2 border-transparent hover:border-playful-teal/20"
    >
      {/* Image Container */}
      <div className="relative h-64 overflow-hidden bg-gray-100">
        <img
          src={image}
          alt={name}
          className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-110"
        />
        <div className="absolute top-4 right-4">
          <button className="p-2 bg-white/80 backdrop-blur-sm rounded-full shadow-sm hover:bg-playful-coral hover:text-white transition-colors duration-300 group/heart">
            <Heart className="w-5 h-5 text-playful-coral group-hover/heart:fill-current group-hover/heart:text-white" />
          </button>
        </div>
        <div className="absolute bottom-0 left-0 w-full p-4 bg-gradient-to-t from-black/60 to-transparent">
          <span className="inline-block px-3 py-1 bg-white/90 backdrop-blur-md rounded-full text-xs font-bold text-playful-text shadow-sm">
            {type === 'dog' ? 'üê∂' : type === 'cat' ? 'üê±' : 'üêæ'} {breed}
          </span>
        </div>
      </div>

      {/* Content */}
      <div className="p-6">
        <div className="flex justify-between items-start mb-2">
          <h3 className="text-2xl font-heading font-bold text-playful-text group-hover:text-playful-teal transition-colors">
            {name}
          </h3>
          <span className="text-sm font-bold bg-playful-lavender text-playful-text px-2 py-1 rounded-lg">
            {typeof age === 'string' ? age : `${age} yr${age === 1 ? '' : 's'}`}
          </span>
        </div>

        <div className="flex items-center text-gray-500 mb-6">
          <MapPin className="w-4 h-4 mr-1 text-playful-coral" />
          <span className="text-sm font-medium">{location}</span>
        </div>

        <Link to={`/pet/${id}`} className="block">
          <PrimaryButton
            variant="secondary"
            className="w-full group-hover:bg-playful-teal group-hover:text-white"
          >
            Meet {name}
          </PrimaryButton>
        </Link>
      </div>
    </motion.div>
  );
};

export default PetCard;

import React from 'react';
import { ChevronLeft, ChevronRight } from 'lucide-react';
import PrimaryButton from '@/components/ui/PrimaryButton';

interface PaginationProps {
    currentPage: number;
    totalPages: number;
    onPageChange: (page: number) => void;
}

const Pagination = ({ currentPage, totalPages, onPageChange }: PaginationProps) => {
    const pageNumbers = [];
    for (let i = 1; i <= totalPages; i++) {
        pageNumbers.push(i);
    }

    return (
        <div className="flex justify-center items-center gap-2 mt-12">
            <button
                onClick={() => onPageChange(currentPage - 1)}
                disabled={currentPage === 1}
                className="p-2 rounded-full hover:bg-playful-cream disabled:opacity-50 disabled:cursor-not-allowed transition-colors text-playful-text"
            >
                <ChevronLeft className="w-6 h-6" />
            </button>

            {pageNumbers.map((number) => (
                <button
                    key={number}
                    onClick={() => onPageChange(number)}
                    className={`w-10 h-10 rounded-full font-bold transition-all duration-300 ${currentPage === number
                            ? 'bg-playful-coral text-white shadow-md scale-110'
                            : 'bg-white text-gray-600 hover:bg-playful-cream'
                        }`}
                >
                    {number}
                </button>
            ))}

            <button
                onClick={() => onPageChange(currentPage + 1)}
                disabled={currentPage === totalPages}
                className="p-2 rounded-full hover:bg-playful-cream disabled:opacity-50 disabled:cursor-not-allowed transition-colors text-playful-text"
            >
                <ChevronRight className="w-6 h-6" />
            </button>
        </div>
    );
};

export default Pagination;

import { motion } from 'framer-motion';
import { PetFilters as PetFiltersType } from '@/hooks/usePets';
import { Filter, RotateCcw } from 'lucide-react';
import PrimaryButton from '@/components/ui/PrimaryButton';

interface PetFiltersProps {
  filters: PetFiltersType;
  onFilterChange: <K extends keyof PetFiltersType>(key: K, value: PetFiltersType[K]) => void;
  onResetFilters: () => void;
  showFilters: boolean;
}

const PetFilters = ({
  filters,
  onFilterChange,
  onResetFilters,
  showFilters
}: PetFiltersProps) => {
  if (!showFilters) return null;

  return (
    <motion.div
      className="bg-white rounded-[2rem] p-8 shadow-soft mb-8 border-2 border-playful-cream"
      initial={{ opacity: 0, height: 0 }}
      animate={{ opacity: 1, height: 'auto' }}
      exit={{ opacity: 0, height: 0 }}
      transition={{ duration: 0.3 }}
    >
      <div className="flex items-center gap-2 mb-6 text-playful-teal">
        <Filter className="w-5 h-5" />
        <h3 className="font-heading font-bold text-lg">Filter Friends</h3>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div>
          <label className="block text-sm font-bold text-gray-600 mb-2 ml-2">Type</label>
          <div className="relative">
            <select
              className="w-full bg-playful-cream border-2 border-transparent hover:border-playful-teal/30 rounded-xl p-3 text-gray-700 font-medium focus:outline-none focus:border-playful-teal focus:ring-4 focus:ring-playful-teal/10 transition-all cursor-pointer appearance-none"
              value={filters.selectedType}
              onChange={(e) => onFilterChange('selectedType', e.target.value as any)}
            >
              <option value="all">All Types üêæ</option>
              <option value="Dog">Dogs üê∂</option>
              <option value="Cat">Cats üê±</option>
              <option value="Bird">Birds üê¶</option>
              <option value="Small Pet">Small Pets üê∞</option>
            </select>
          </div>
        </div>

        <div>
          <label className="block text-sm font-bold text-gray-600 mb-2 ml-2">Age</label>
          <div className="relative">
            <select
              className="w-full bg-playful-cream border-2 border-transparent hover:border-playful-coral/30 rounded-xl p-3 text-gray-700 font-medium focus:outline-none focus:border-playful-coral focus:ring-4 focus:ring-playful-coral/10 transition-all cursor-pointer appearance-none"
              value={filters.selectedAge}
              onChange={(e) => onFilterChange('selectedAge', e.target.value as any)}
            >
              <option value="all">All Ages üéÇ</option>
              <option value="baby">Baby (0-1 yr)</option>
              <option value="young">Young (1-3 yrs)</option>
              <option value="adult">Adult (3-8 yrs)</option>
              <option value="senior">Senior (8+ yrs)</option>
            </select>
          </div>
        </div>
      </div>

      <div className="mt-6 flex flex-col sm:flex-row justify-between items-center gap-4">
        <label className="inline-flex items-center cursor-pointer group">
          <div className="relative">
            <input
              type="checkbox"
              className="sr-only peer"
              checked={filters.showUrgentOnly}
              onChange={(e) => onFilterChange('showUrgentOnly', e.target.checked)}
            />
            <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-playful-coral/20 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-playful-coral"></div>
          </div>
          <span className="ml-3 text-gray-600 font-medium group-hover:text-playful-coral transition-colors">Show Urgent Needs Only ‚ù§Ô∏è</span>
        </label>

        <PrimaryButton
          variant="ghost"
          size="sm"
          onClick={onResetFilters}
          className="text-gray-400 hover:text-gray-600"
        >
          <RotateCcw className="w-4 h-4 mr-2" />
          Reset Filters
        </PrimaryButton>
      </div>
    </motion.div>
  );
};

export default PetFilters;

import React from 'react';
import { motion } from 'framer-motion';
import { PawPrint } from 'lucide-react';
import PetCard from '@/components/PetCard';
import { Pet } from '@/hooks/usePets';
import Pagination from './Pagination';

interface PetGridProps {
  pets: Pet[];
  isLoading: boolean;
  currentPage: number;
  petsPerPage: number;
  totalPages: number;
  onPageChange: (page: number) => void;
  sortBy: string;
  onSortChange: (sort: string) => void;
}

const PetGrid = ({
  pets,
  isLoading,
  currentPage,
  petsPerPage,
  totalPages,
  onPageChange,
  sortBy,
  onSortChange,
}: PetGridProps) => {
  const fadeInStagger = {
    hidden: { opacity: 0, y: 20 },
    visible: (custom: number) => ({
      opacity: 1,
      y: 0,
      transition: { delay: custom * 0.1, duration: 0.5 },
    }),
  };

  if (isLoading) {
    return (
      <div className="flex justify-center items-center py-20">
        <motion.div
          animate={{
            scale: [1, 1.2, 1],
            opacity: [0.5, 1, 0.5]
          }}
          transition={{
            duration: 1.5,
            repeat: Infinity,
            ease: "easeInOut"
          }}
        >
          <PawPrint className="w-16 h-16 text-playful-coral" />
        </motion.div>
      </div>
    );
  }

  if (pets.length === 0) {
    return (
      <div className="text-center py-16">
        <motion.p
          className="text-xl text-gray-600 mb-4 font-medium"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.2 }}
        >
          No pets found matching your criteria üêæ
        </motion.p>
      </div>
    );
  }

  return (
    <div>
      <div className="flex flex-col sm:flex-row justify-between items-center mb-8 gap-4">
        <p className="text-xl font-heading font-bold text-playful-text">
          Found <span className="text-playful-coral">{pets.length}</span> friends
        </p>
        <div className="flex items-center gap-3">
          <span className="text-sm font-bold text-gray-500 hidden md:inline">Sort by:</span>
          <select
            className="bg-white border-2 border-gray-200 rounded-full px-4 py-2 font-medium text-gray-700 focus:outline-none focus:border-playful-teal focus:ring-2 focus:ring-playful-teal/20 transition-all duration-200 cursor-pointer"
            value={sortBy}
            onChange={(e) => onSortChange(e.target.value)}
          >
            <option value="newest">Newest First</option>
            <option value="oldest">Oldest First</option>
            <option value="nameAsc">Name (A-Z)</option>
            <option value="nameDesc">Name (Z-A)</option>
          </select>
        </div>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        {pets.map((pet, index) => (
          <motion.div
            key={pet.id}
            initial="hidden"
            animate="visible"
            variants={fadeInStagger}
            custom={index}
          >
            <PetCard
              id={pet.id}
              name={pet.pet_name}
              breed={pet.breed}
              age={pet.age || '0 years'}
              location={pet.location}
              image={pet.image_url || '/placeholder.svg'}
              type={pet.animal_type || 'dog'}
            />
          </motion.div>
        ))}
      </div>

      {totalPages > 1 && (
        <Pagination currentPage={currentPage} totalPages={totalPages} onPageChange={onPageChange} />
      )}
    </div>
  );
};

export default PetGrid;

import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { ArrowRight } from 'lucide-react';
import { motion } from 'framer-motion';
import { supabase } from '@/integrations/supabase/client';
import PetCard from '@/components/PetCard';
import PrimaryButton from '@/components/ui/PrimaryButton';
import ScrollReveal from '@/components/ui/ScrollReveal';

interface Pet {
    id: any;
    pet_name: string;
    image_url: string;
    breed: string;
    location: string;
    status: string;
    animal_type?: string;
    age: number | string;
}

const PlayfulFeaturedPets = () => {
    const [featuredPets, setFeaturedPets] = useState<Pet[]>([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        fetchLatestPets();
    }, []);

    const fetchLatestPets = async () => {
        setIsLoading(true);
        try {
            const { data, error } = await supabase
                .from('pet_listings')
                .select('*')
                .order('created_at', { ascending: false })
                .limit(4);

            if (error) {
                console.error('Error fetching pet listings:', error);
                return;
            }

            if (data) {
                setFeaturedPets(data);
            }
        } catch (error) {
            console.error('Failed to fetch pet listings:', error);
        } finally {
            setIsLoading(false);
        }
    };

    if (isLoading) {
        return (
            <div className="flex justify-center items-center py-20">
                <div className="w-16 h-16 border-4 border-playful-coral border-t-transparent rounded-full animate-spin"></div>
            </div>
        );
    }

    return (
        <section className="py-20 bg-playful-cream">
            <div className="container mx-auto px-4">
                <ScrollReveal
                    mode="fade-up"
                    width="100%"
                    className="text-center mb-16"
                >
                    <span className="inline-block px-4 py-1 bg-playful-mint/30 text-teal-800 rounded-full font-bold text-sm tracking-wide mb-4">
                        Meet Our Stars
                    </span>
                    <h2 className="text-4xl md:text-5xl font-heading font-bold text-playful-text mb-6">
                        Featured <span className="text-playful-coral">Friends</span>
                    </h2>
                    <p className="text-xl text-gray-600 max-w-2xl mx-auto">
                        These adorable pets are looking for their forever homes. Could you be the one they've been waiting for?
                    </p>
                </ScrollReveal>

                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8 mb-16">
                    {featuredPets.map((pet, index) => (
                        <ScrollReveal
                            key={pet.id}
                            mode="fade-up"
                            delay={index * 0.1}
                            width="100%"
                            className="h-full"
                        >
                            <PetCard
                                id={pet.id}
                                name={pet.pet_name}
                                breed={pet.breed}
                                age={typeof pet.age === 'number' ? pet.age : parseInt(pet.age as string) || 2}
                                location={pet.location}
                                image={pet.image_url || "/placeholder.svg"}
                                type={pet.animal_type || 'dog'}
                            />
                        </ScrollReveal>
                    ))}
                </div>

                <div className="text-center">
                    <Link to="/browse">
                        <PrimaryButton size="lg" className="group">
                            View All Pets
                            <ArrowRight className="ml-2 h-5 w-5 group-hover:translate-x-1 transition-transform" />
                        </PrimaryButton>
                    </Link>
                </div>
            </div>
        </section>
    );
};

export default PlayfulFeaturedPets;

import React from 'react';
import { Search, Filter } from 'lucide-react';
import PrimaryButton from '@/components/ui/PrimaryButton';

interface PlayfulSearchSectionProps {
    searchQuery: string;
    setSearchQuery: (query: string) => void;
    handleSearch: (type?: string) => void;
    handleKeyPress: (e: React.KeyboardEvent) => void;
}

const PlayfulSearchSection = ({
    searchQuery,
    setSearchQuery,
    handleSearch,
    handleKeyPress
}: PlayfulSearchSectionProps) => {

    const categories = [
        { name: 'Dogs', icon: 'üê∂', type: 'dog', color: 'bg-playful-coral' },
        { name: 'Cats', icon: 'üê±', type: 'cat', color: 'bg-playful-teal' },
        { name: 'Birds', icon: 'üê¶', type: 'bird', color: 'bg-playful-yellow' },
        { name: 'Rabbits', icon: 'üê∞', type: 'rabbit', color: 'bg-playful-mint' },
    ];

    return (
        <section className="py-16 px-4 bg-white relative z-10 -mt-10 rounded-t-[3rem]">
            <div className="container mx-auto max-w-4xl">
                <div className="bg-white rounded-[2rem] shadow-xl p-8 border border-gray-100">
                    <h2 className="text-3xl font-heading font-bold text-center mb-8 text-playful-text">
                        Who are you looking for?
                    </h2>

                    {/* Search Bar */}
                    <div className="relative max-w-2xl mx-auto mb-10 group">
                        <div className="absolute inset-y-0 left-4 flex items-center pointer-events-none">
                            <Search className="h-6 w-6 text-gray-400 group-focus-within:text-playful-coral transition-colors" />
                        </div>
                        <input
                            type="text"
                            className="w-full pl-12 pr-4 py-4 rounded-full border-2 border-gray-200 focus:border-playful-coral focus:ring-4 focus:ring-playful-coral/20 outline-none text-lg transition-all shadow-sm"
                            placeholder="Search by breed, name, or location..."
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                            onKeyDown={handleKeyPress}
                        />
                        <div className="absolute inset-y-1 right-1">
                            <PrimaryButton onClick={() => handleSearch()} size="md" className="h-full rounded-full px-4 md:px-6">
                                <span className="hidden md:inline">Search</span>
                                <Search className="md:hidden h-5 w-5" />
                            </PrimaryButton>
                        </div>
                    </div>

                    {/* Categories */}
                    <div className="flex flex-wrap justify-center gap-4">
                        {categories.map((cat) => (
                            <button
                                key={cat.name}
                                onClick={() => handleSearch(cat.type)}
                                className="flex flex-col items-center gap-2 group"
                            >
                                <div className={`w-16 h-16 rounded-2xl flex items-center justify-center text-3xl shadow-md transition-transform duration-300 group-hover:-translate-y-2 group-hover:rotate-3 ${cat.color} bg-opacity-20 group-hover:bg-opacity-100`}>
                                    {cat.icon}
                                </div>
                                <span className="font-bold text-gray-600 group-hover:text-playful-text">{cat.name}</span>
                            </button>
                        ))}
                    </div>
                </div>
            </div>
        </section>
    );
};

export default PlayfulSearchSection;

import React from 'react';
import { motion } from 'framer-motion';
import { X, Heart } from 'lucide-react';
import PrimaryButton from '@/components/ui/PrimaryButton';
import LoadingSpinner from '@/components/LoadingSpinner';
import { Pet } from '@/hooks/usePets';

interface AdoptionModalProps {
  pet: Pet;
  onClose: () => void;
  isSubmitting: boolean;
  setIsSubmitting: (isSubmitting: boolean) => void;
  setShowSuccess: (showSuccess: boolean) => void;
}

const AdoptionModal = ({
  pet,
  onClose,
  isSubmitting,
  setIsSubmitting,
  setShowSuccess,
}: AdoptionModalProps) => {
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Simulate API call
    setTimeout(() => {
      setIsSubmitting(false);
      setShowSuccess(true);
    }, 1500);
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/50 backdrop-blur-sm"
    >
      <motion.div
        initial={{ scale: 0.9, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        exit={{ scale: 0.9, opacity: 0 }}
        className="bg-white rounded-[2rem] p-8 max-w-lg w-full shadow-2xl relative overflow-hidden"
      >
        {/* Decorative background */}
        <div className="absolute top-0 right-0 w-32 h-32 bg-playful-coral/10 rounded-full -mr-10 -mt-10 blur-2xl"></div>
        <div className="absolute bottom-0 left-0 w-32 h-32 bg-playful-teal/10 rounded-full -ml-10 -mb-10 blur-2xl"></div>

        <button
          onClick={onClose}
          className="absolute top-4 right-4 p-2 rounded-full hover:bg-gray-100 transition-colors"
        >
          <X className="w-6 h-6 text-gray-500" />
        </button>

        <div className="text-center mb-8 relative z-10">
          <div className="inline-block p-3 bg-playful-coral/10 rounded-full mb-4">
            <Heart className="w-8 h-8 text-playful-coral fill-current" />
          </div>
          <h2 className="text-3xl font-heading font-bold text-playful-text mb-2">
            Adopt {pet.name || pet.pet_name}
          </h2>
          <p className="text-gray-600">Fill out the form below to start your adoption journey!</p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4 relative z-10">
          <div>
            <label className="block text-sm font-bold text-gray-700 mb-1 ml-1">Full Name</label>
            <input
              type="text"
              required
              className="w-full px-4 py-3 rounded-xl bg-gray-50 border-2 border-transparent focus:bg-white focus:border-playful-teal focus:outline-none transition-all"
              placeholder="John Doe"
            />
          </div>

          <div>
            <label className="block text-sm font-bold text-gray-700 mb-1 ml-1">Email Address</label>
            <input
              type="email"
              required
              className="w-full px-4 py-3 rounded-xl bg-gray-50 border-2 border-transparent focus:bg-white focus:border-playful-teal focus:outline-none transition-all"
              placeholder="john@example.com"
            />
          </div>

          <div>
            <label className="block text-sm font-bold text-gray-700 mb-1 ml-1">Phone Number</label>
            <input
              type="tel"
              required
              className="w-full px-4 py-3 rounded-xl bg-gray-50 border-2 border-transparent focus:bg-white focus:border-playful-teal focus:outline-none transition-all"
              placeholder="(555) 123-4567"
            />
          </div>

          <div>
            <label className="block text-sm font-bold text-gray-700 mb-1 ml-1">
              Why do you want to adopt?
            </label>
            <textarea
              required
              rows={3}
              className="w-full px-4 py-3 rounded-xl bg-gray-50 border-2 border-transparent focus:bg-white focus:border-playful-teal focus:outline-none transition-all resize-none"
              placeholder="Tell us a bit about yourself..."
            />
          </div>

          <div className="pt-4">
            <PrimaryButton
              type="submit"
              className="w-full justify-center text-lg py-4"
              disabled={isSubmitting}
            >
              {isSubmitting ? (
                <span className="flex items-center justify-center">
                  <svg className="animate-spin h-5 w-5 mr-2" viewBox="0 0 24 24">
                    <circle
                      className="opacity-25"
                      cx="12"
                      cy="12"
                      r="10"
                      stroke="currentColor"
                      strokeWidth="4"
                      fill="none"
                    ></circle>
                    <path
                      className="opacity-75"
                      fill="currentColor"
                      d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                    ></path>
                  </svg>
                  Submitting...
                </span>
              ) : (
                'Submit Application üöÄ'
              )}
            </PrimaryButton>
          </div>
        </form>
      </motion.div>
    </motion.div>
  );
};

export default AdoptionModal;

import { motion } from 'framer-motion';
import { Phone, Mail, MapPin, Clock, User } from 'lucide-react';
import { useEffect, useState } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { Skeleton } from '@/components/ui/skeleton';

interface ContactInfoCardProps {
  petName?: string;
  petId?: string;
  petLocation?: string;
}

type CaregiverInfo = {
  caregiver_name: string;
  mobile: string;
  location: string;
};

const ContactInfoCard = ({ petName, petId, petLocation }: ContactInfoCardProps) => {
  const [caregiverInfo, setCaregiverInfo] = useState<CaregiverInfo | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchCaregiverInfo = async () => {
      if (!petId) {
        setIsLoading(false);
        return;
      }

      try {
        const { data, error } = await supabase
          .from('pet_listings')
          .select('caregiver_name, mobile, location')
          .eq('id', petId)
          .single();

        if (error) {
          console.error('Error fetching caregiver info:', error);
          return;
        }

        setCaregiverInfo(data);
      } catch (error) {
        console.error('Failed to fetch caregiver info:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchCaregiverInfo();
  }, [petId]);

  return (
    <motion.div
      initial={{ opacity: 0, x: 20 }}
      animate={{ opacity: 1, x: 0 }}
      transition={{ duration: 0.6, delay: 0.4 }}
      className="bg-white rounded-[2rem] p-8 shadow-soft border border-gray-100 lg:w-96 flex-shrink-0 h-fit sticky top-24"
    >
      <h3 className="text-2xl font-heading font-bold text-playful-text mb-6">
        Contact Information
      </h3>

      <div className="space-y-6">
        <div className="flex items-start gap-4">
          <div className="bg-playful-coral/10 p-3 rounded-full text-playful-coral">
            <User className="w-5 h-5" />
          </div>
          <div>
            <p className="font-bold text-gray-800">Caregiver</p>
            {isLoading ? (
              <Skeleton className="h-5 w-32 mt-1" />
            ) : (
              <p className="text-gray-600">{caregiverInfo?.caregiver_name || 'Not available'}</p>
            )}
          </div>
        </div>

        <div className="flex items-start gap-4">
          <div className="bg-playful-teal/10 p-3 rounded-full text-playful-teal">
            <Phone className="w-5 h-5" />
          </div>
          <div>
            <p className="font-bold text-gray-800">Phone</p>
            {isLoading ? (
              <Skeleton className="h-5 w-32 mt-1" />
            ) : (
              <p className="text-gray-600">{caregiverInfo?.mobile || 'Not available'}</p>
            )}
          </div>
        </div>

        <div className="flex items-start gap-4">
          <div className="bg-playful-lavender/30 p-3 rounded-full text-purple-600">
            <MapPin className="w-5 h-5" />
          </div>
          <div>
            <p className="font-bold text-gray-800">Location</p>
            {isLoading ? (
              <Skeleton className="h-5 w-32 mt-1" />
            ) : (
              <p className="text-gray-600">
                {caregiverInfo?.location || petLocation || 'Not available'}
              </p>
            )}
          </div>
        </div>

        <div className="flex items-start gap-4">
          <div className="bg-playful-yellow/20 p-3 rounded-full text-yellow-600">
            <Clock className="w-5 h-5" />
          </div>
          <div>
            <p className="font-bold text-gray-800">Availability</p>
            <p className="text-gray-600">Contact for details</p>
          </div>
        </div>
      </div>

      {/* Map Placeholder */}
      <div className="mt-8 rounded-2xl overflow-hidden h-48 bg-gray-100 relative group">
        <img
          src="https://images.unsplash.com/photo-1524661135-423995f22d0b?auto=format&fit=crop&q=80&w=600"
          alt="Map location"
          className="w-full h-full object-cover opacity-80 group-hover:opacity-100 transition-opacity duration-300"
        />
        <div className="absolute inset-0 flex items-center justify-center bg-black/10 group-hover:bg-transparent transition-colors">
          <div className="bg-white p-2 rounded-full shadow-lg">
            <MapPin className="w-6 h-6 text-playful-coral" />
          </div>
        </div>
      </div>
    </motion.div>
  );
};

export default ContactInfoCard;

import { useState } from "react";
import { motion } from "framer-motion";
import { toast } from "sonner";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { supabase, generateUUID } from "@/integrations/supabase/client";
import { Upload, X, PawPrint, User, Phone, MapPin, FileText, Heart, ShieldCheck } from "lucide-react";
import PrimaryButton from "@/components/ui/PrimaryButton";
import SuccessDialog from "./form-sections/SuccessDialog";

interface PetListingFormProps {
    open?: boolean;
    onOpenChange?: (open: boolean) => void;
    isPage?: boolean;
}

const PetListingForm = ({ open = true, onOpenChange = () => { }, isPage = false }: PetListingFormProps) => {
    const [formData, setFormData] = useState({
        caregiverName: "",
        mobile: "",
        petName: "",
        location: "",
        animalType: "",
        breed: "",
        medicalInfo: "",
        description: "",
        age: "",
        agreeToTerms: false,
        agreeToPrivacyPolicy: false
    });

    const [image, setImage] = useState<File | null>(null);
    const [previewUrl, setPreviewUrl] = useState<string | null>(null);
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [showSuccessDialog, setShowSuccessDialog] = useState(false);

    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSelectChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
        setFormData(prev => ({ ...prev, animalType: e.target.value }));
    };

    const handleCheckboxChange = (name: string, checked: boolean) => {
        setFormData(prev => ({ ...prev, [name]: checked }));
    };

    const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        if (file) {
            setImage(file);
            const url = URL.createObjectURL(file);
            setPreviewUrl(url);
        }
    };

    const handleRemoveImage = () => {
        setImage(null);
        setPreviewUrl(null);
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();

        if (!formData.agreeToTerms) {
            toast.error("Please agree to the Terms and Conditions");
            return;
        }

        if (!formData.agreeToPrivacyPolicy) {
            toast.error("Please agree to the Privacy Policy");
            return;
        }

        if (!image) {
            toast.error("Please upload a pet image");
            return;
        }

        if (!formData.location) {
            toast.error("Please enter a location");
            return;
        }

        if (!formData.age) {
            toast.error("Please enter the pet's age");
            return;
        }

        setIsSubmitting(true);

        try {
            const petId = generateUUID();

            let imageUrl = null;
            if (image) {
                const fileExt = image.name.split('.').pop();
                const fileName = `${Math.random().toString(36).substring(2, 15)}.${fileExt}`;

                const { error: uploadError, data } = await supabase.storage
                    .from('pet-images')
                    .upload(fileName, image);

                if (uploadError) {
                    throw uploadError;
                }

                if (data) {
                    const { data: urlData } = supabase.storage.from('pet-images').getPublicUrl(fileName);
                    imageUrl = urlData.publicUrl;
                }
            }

            const { error } = await supabase.from('pet_listings').insert({
                id: petId,
                caregiver_name: formData.caregiverName,
                mobile: formData.mobile,
                pet_name: formData.petName,
                location: formData.location,
                animal_type: formData.animalType,
                breed: formData.breed,
                medical_info: formData.medicalInfo,
                description: formData.description,
                age: formData.age,
                image_url: imageUrl,
                status: 'pending'
            });

            if (error) {
                console.error('Supabase error:', error);
                throw error;
            }

            setShowSuccessDialog(true);
            resetForm();

        } catch (error) {
            console.error('Error submitting pet listing:', error);
            toast.error("Failed to submit listing. Please try again.");
        } finally {
            setIsSubmitting(false);
            if (!isPage) {
                onOpenChange(false);
            }
        }
    };

    const resetForm = () => {
        setFormData({
            caregiverName: "",
            mobile: "",
            petName: "",
            location: "",
            animalType: "",
            breed: "",
            medicalInfo: "",
            description: "",
            age: "",
            agreeToTerms: false,
            agreeToPrivacyPolicy: false
        });
        setImage(null);
        setPreviewUrl(null);
    };

    const formContent = (
        <form onSubmit={handleSubmit} className="space-y-8">
            {/* Caregiver Info */}
            <div className="space-y-6">
                <div className="flex items-center gap-3 border-b-2 border-playful-yellow/30 pb-2">
                    <div className="bg-playful-yellow/20 p-2 rounded-full text-yellow-600">
                        <User className="h-5 w-5" />
                    </div>
                    <h3 className="text-xl font-heading font-bold text-playful-text">Caregiver Information</h3>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="space-y-2">
                        <label htmlFor="caregiverName" className="text-sm font-bold text-gray-700 ml-1">
                            Your Name *
                        </label>
                        <input
                            id="caregiverName"
                            name="caregiverName"
                            value={formData.caregiverName}
                            onChange={handleInputChange}
                            required
                            className="w-full bg-gray-50 border-2 border-gray-200 rounded-2xl p-4 font-medium text-gray-800 focus:outline-none focus:border-playful-yellow focus:ring-4 focus:ring-playful-yellow/10 transition-all duration-200"
                            placeholder="Enter your name"
                        />
                    </div>

                    <div className="space-y-2">
                        <label htmlFor="mobile" className="text-sm font-bold text-gray-700 ml-1">
                            Mobile Number *
                        </label>
                        <div className="relative">
                            <Phone className="absolute left-4 top-1/2 -translate-y-1/2 text-gray-400 h-5 w-5" />
                            <input
                                id="mobile"
                                name="mobile"
                                type="tel"
                                value={formData.mobile}
                                onChange={handleInputChange}
                                required
                                className="w-full bg-gray-50 border-2 border-gray-200 rounded-2xl p-4 pl-12 font-medium text-gray-800 focus:outline-none focus:border-playful-yellow focus:ring-4 focus:ring-playful-yellow/10 transition-all duration-200"
                                placeholder="Enter mobile number"
                            />
                        </div>
                    </div>
                </div>
            </div>

            {/* Pet Info */}
            <div className="space-y-6">
                <div className="flex items-center gap-3 border-b-2 border-playful-mint/30 pb-2">
                    <div className="bg-playful-mint/20 p-2 rounded-full text-green-600">
                        <PawPrint className="h-5 w-5" />
                    </div>
                    <h3 className="text-xl font-heading font-bold text-playful-text">Pet Information</h3>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="space-y-2">
                        <label htmlFor="petName" className="text-sm font-bold text-gray-700 ml-1">
                            Pet Name *
                        </label>
                        <input
                            id="petName"
                            name="petName"
                            value={formData.petName}
                            onChange={handleInputChange}
                            required
                            className="w-full bg-gray-50 border-2 border-gray-200 rounded-2xl p-4 font-medium text-gray-800 focus:outline-none focus:border-playful-mint focus:ring-4 focus:ring-playful-mint/10 transition-all duration-200"
                            placeholder="Enter pet name"
                        />
                    </div>

                    <div className="space-y-2">
                        <label htmlFor="age" className="text-sm font-bold text-gray-700 ml-1">
                            Pet Age *
                        </label>
                        <input
                            id="age"
                            name="age"
                            value={formData.age}
                            onChange={handleInputChange}
                            required
                            className="w-full bg-gray-50 border-2 border-gray-200 rounded-2xl p-4 font-medium text-gray-800 focus:outline-none focus:border-playful-mint focus:ring-4 focus:ring-playful-mint/10 transition-all duration-200"
                            placeholder="e.g., 2 years, 6 months"
                        />
                    </div>

                    <div className="space-y-2">
                        <label htmlFor="location" className="text-sm font-bold text-gray-700 ml-1">
                            Location *
                        </label>
                        <div className="relative">
                            <MapPin className="absolute left-4 top-1/2 -translate-y-1/2 text-gray-400 h-5 w-5" />
                            <input
                                id="location"
                                name="location"
                                value={formData.location}
                                onChange={handleInputChange}
                                required
                                className="w-full bg-gray-50 border-2 border-gray-200 rounded-2xl p-4 pl-12 font-medium text-gray-800 focus:outline-none focus:border-playful-mint focus:ring-4 focus:ring-playful-mint/10 transition-all duration-200"
                                placeholder="City, State"
                            />
                        </div>
                    </div>

                    <div className="space-y-2">
                        <label htmlFor="animalType" className="text-sm font-bold text-gray-700 ml-1">
                            Animal Type *
                        </label>
                        <select
                            id="animalType"
                            value={formData.animalType}
                            onChange={handleSelectChange}
                            className="w-full bg-gray-50 border-2 border-gray-200 rounded-2xl p-4 font-medium text-gray-800 focus:outline-none focus:border-playful-mint focus:ring-4 focus:ring-playful-mint/10 transition-all duration-200 appearance-none"
                        >
                            <option value="">Select Animal Type</option>
                            <option value="Dog">Dog</option>
                            <option value="Cat">Cat</option>
                            <option value="Bird">Bird</option>
                            <option value="Small Pet">Small Pet</option>
                            <option value="Other">Other</option>
                        </select>
                    </div>

                    <div className="space-y-2">
                        <label htmlFor="breed" className="text-sm font-bold text-gray-700 ml-1">
                            Breed / Species *
                        </label>
                        <input
                            id="breed"
                            name="breed"
                            value={formData.breed}
                            onChange={handleInputChange}
                            required
                            className="w-full bg-gray-50 border-2 border-gray-200 rounded-2xl p-4 font-medium text-gray-800 focus:outline-none focus:border-playful-mint focus:ring-4 focus:ring-playful-mint/10 transition-all duration-200"
                            placeholder="Enter breed or species"
                        />
                    </div>

                    <div className="space-y-2">
                        <label htmlFor="medicalInfo" className="text-sm font-bold text-gray-700 ml-1">
                            Medical History *
                        </label>
                        <input
                            id="medicalInfo"
                            name="medicalInfo"
                            value={formData.medicalInfo}
                            onChange={handleInputChange}
                            required
                            className="w-full bg-gray-50 border-2 border-gray-200 rounded-2xl p-4 font-medium text-gray-800 focus:outline-none focus:border-playful-mint focus:ring-4 focus:ring-playful-mint/10 transition-all duration-200"
                            placeholder="Vaccinations, spayed/neutered, etc."
                        />
                    </div>
                </div>

                <div className="space-y-2">
                    <label htmlFor="description" className="text-sm font-bold text-gray-700 ml-1">
                        Description *
                    </label>
                    <textarea
                        id="description"
                        name="description"
                        value={formData.description}
                        onChange={handleInputChange}
                        placeholder="Tell us about the pet's personality, habits, and needs..."
                        className="w-full bg-gray-50 border-2 border-gray-200 rounded-2xl p-4 font-medium text-gray-800 focus:outline-none focus:border-playful-mint focus:ring-4 focus:ring-playful-mint/10 transition-all duration-200 min-h-32 resize-none"
                        required
                    />
                </div>
            </div>

            {/* Photo Upload */}
            <div className="space-y-6">
                <div className="flex items-center gap-3 border-b-2 border-playful-coral/30 pb-2">
                    <div className="bg-playful-coral/20 p-2 rounded-full text-playful-coral">
                        <Upload className="h-5 w-5" />
                    </div>
                    <h3 className="text-xl font-heading font-bold text-playful-text">Pet Photo</h3>
                </div>

                {!previewUrl ? (
                    <div className="border-4 border-dashed border-gray-200 rounded-[2rem] p-8 text-center hover:border-playful-coral/50 transition-colors bg-gray-50">
                        <div className="bg-white p-4 w-20 h-20 mx-auto mb-4 flex items-center justify-center rounded-full shadow-soft text-playful-coral">
                            <Upload className="h-10 w-10" />
                        </div>
                        <p className="text-lg font-bold text-gray-700 mb-2">
                            Upload a clear photo
                        </p>
                        <p className="text-gray-500 text-sm mb-6">
                            High-quality photos help pets get adopted faster!
                        </p>
                        <label className="inline-block cursor-pointer">
                            <span className="bg-playful-coral hover:bg-playful-coral/90 text-white px-6 py-3 rounded-full font-bold shadow-lg shadow-playful-coral/20 transition-all hover:-translate-y-1 inline-flex items-center gap-2">
                                <Upload className="h-5 w-5" />
                                Choose Photo
                            </span>
                            <input
                                type="file"
                                accept="image/*"
                                onChange={handleFileChange}
                                className="hidden"
                                required
                            />
                        </label>
                    </div>
                ) : (
                    <div className="relative rounded-[2rem] overflow-hidden shadow-lg border-4 border-white">
                        <img
                            src={previewUrl}
                            alt="Pet preview"
                            className="w-full h-64 object-cover"
                        />
                        <button
                            type="button"
                            onClick={handleRemoveImage}
                            className="absolute top-4 right-4 bg-white/90 text-red-500 p-2 rounded-full shadow-md hover:bg-red-50 transition-colors"
                        >
                            <X className="h-6 w-6" />
                        </button>
                        <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/50 to-transparent p-4 text-white text-center font-bold">
                            Looking good! üì∏
                        </div>
                    </div>
                )}
            </div>

            {/* Terms & Privacy */}
            <div className="space-y-6">
                <div className="flex items-center gap-3 border-b-2 border-playful-teal/30 pb-2">
                    <div className="bg-playful-teal/20 p-2 rounded-full text-playful-teal">
                        <ShieldCheck className="h-5 w-5" />
                    </div>
                    <h3 className="text-xl font-heading font-bold text-playful-text">Terms & Privacy</h3>
                </div>

                <div className="space-y-4 bg-gray-50 p-6 rounded-2xl border-2 border-gray-100">
                    <label className="flex items-start gap-3 cursor-pointer group">
                        <div className="relative flex items-center">
                            <input
                                type="checkbox"
                                checked={formData.agreeToTerms}
                                onChange={(e) => handleCheckboxChange('agreeToTerms', e.target.checked)}
                                className="peer h-6 w-6 appearance-none border-2 border-gray-300 rounded-lg checked:bg-playful-teal checked:border-playful-teal transition-all"
                            />
                            <Heart className="absolute left-1 top-1 h-4 w-4 text-white opacity-0 peer-checked:opacity-100 pointer-events-none transition-opacity" />
                        </div>
                        <span className="text-gray-600 pt-0.5 group-hover:text-gray-900 transition-colors">
                            I agree to the <span className="text-playful-teal font-bold hover:underline">Terms and Conditions</span>
                        </span>
